mapper.select:
SELECT board_no, board_id, board_content, username, board_title, to_char(board_date, 'YYYY-MM-DD HH24:MI:SS')as board_date,board_count,board_recomm FROM boardtable
where board_title like '%' || #{board_title} || '%'
order by board_no desc, board_date desc limit 0,12;

mapper.insert:

	select board_no_seq.nextval from dual



insert into boardtable (board_id,board_no, username,
board_title,board_content
,board_file
)
values(
#{board_id},#{board_no},#{username},#{board_title},#{board_content}
,#{board_file}
)
